import React, { useState } from "react";

import { Helmet } from "react-helmet";
import { Card, ListGroup, ListGroupItem, Row, Col } from "react-bootstrap";
import { paginate } from "../Pagination/paginate";
import Pagination from "../Pagination/Pagination";

const Skills = () => {
  const [pros] = useState([
    { id: 1, title: "Frontend" },
    { id: 2, title: "Backend" },
    { id: 3, title: "Database" },
    { id: 4, title: "Languages" }
  ]);

  const [perPage] = useState(2);
  const [currentPage, setCurrentPage] = useState(1);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const archiveCourses = paginate(pros, currentPage, perPage);

  const knowledgePageTitle = "Knowledge/Amirreza Ghanei";

  return (
    <Col className="mt-5">
      <Helmet>
        <title>{knowledgePageTitle}</title>
      </Helmet>
      <Row className="justify-content-around">
        {archiveCourses}
        <Card
          style={{ width: "40%" }}
          className="col-sm-12 col-md-6 col-lg-5 mt-3"
        >
          <Card.Body>
            <Card.Title>Card Title</Card.Title>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroupItem>Cras justo odio</ListGroupItem>
            <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
            <ListGroupItem>Vestibulum at eros</ListGroupItem>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">Card Link</Card.Link>
            <Card.Link href="#">Another Link</Card.Link>
          </Card.Body>
        </Card>
        <Card
          style={{ width: "40%" }}
          className="col-sm-12 col-md-6 col-lg-5 mt-3 mb-5"
        >
          <Card.Body>
            <Card.Title>Card Title</Card.Title>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroupItem>Cras justo odio</ListGroupItem>
            <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
            <ListGroupItem>Vestibulum at eros</ListGroupItem>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">Card Link</Card.Link>
            <Card.Link href="#">Another Link</Card.Link>
          </Card.Body>
        </Card>
      </Row>
      <Pagination
        totalPros={pros.length}
        currentPage={currentPage}
        perPage={perPage}
        onPageChange={handlePageChange}
      />
    </Col>
  );
};

export default Skills;
