import React, { useState } from "react";

import { Helmet } from "react-helmet";
import { Card, ListGroup, ListGroupItem, Row, Col } from "react-bootstrap";
import { paginate } from "../Pagination/paginate";
import Pagination from "../Pagination/Pagination";

const Skills = () => {
  const [pros] = useState([
    {
      id: 1,
      title: "Frontend",
      one: "HTML",
      two: "CSS/SASS",
      three: "BOOTSTRAP",
      four: "JAVASCRIPT",
      five: "REACT.JS",
      six: "VUE.JS",
      seven: "PUG",
      eight: "REACT.NATIVE"
    },
    {
      id: 2,
      title: "Backend",
      one: "NODE.JS",
      two: "EXPRESS",
      three: "",
      four: "",
      five: "",
      six: "",
      seven: "",
      eight: ""
    },
    {
      id: 3,
      title: "Database",
      one: "MONGODB",
      two: "MYSQL",
      three: "",
      four: "",
      five: "",
      six: "",
      seven: "",
      eight: ""
    },
    {
      id: 4,
      title: "Languages",
      one: "FARSI",
      two: "GERMAN",
      three: "ENGLISH",
      four: "TURKISH",
      five: "",
      six: "",
      seven: "",
      eight: ""
    }
  ]);


  const [perPage] = useState(2);
  const [currentPage, setCurrentPage] = useState(1);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const archiveCourses = paginate(pros, currentPage, perPage);

  const knowledgePageTitle = "Knowledge/Amirreza Ghanei";

  return (
    <Col className="mt-5">
      <Helmet>
        <title>{knowledgePageTitle}</title>
      </Helmet>
      <Row className="justify-content-around">
        {archiveCourses.map((skill) => (
          <Card
            key={skill.id}
            className="col-sm-12 col-md-6 col-lg-5 mt-3 shadow"
          >
            <Card.Body style={{flex: "none"}}>
              <Card.Title className="text-info">{skill.title}</Card.Title>
            </Card.Body>
            <ListGroup className="list-group-flush">
              {skill.one !== "" ? (
                <ListGroupItem>{skill.one}<span>{skill.one === "HTML" ? "100%" : null}</span></ListGroupItem>
              ) : null}{" "}



              {skill.two !== "" ? (
                <ListGroupItem>{skill.t}</ListGroupItem>
              ) : null}{" "}


              {skill.three !== "" ? (
                <ListGroupItem>{skill.three}</ListGroupItem>
              ) : null}
              {skill.four !== "" ? (
                <ListGroupItem>{skill.four}</ListGroupItem>
              ) : null}
              {skill.five !== "" ? (
                <ListGroupItem>{skill.five}</ListGroupItem>
              ) : null}
              {skill.six !== "" ? (
                <ListGroupItem>{skill.six}</ListGroupItem>
              ) : null}
              {skill.seven !== "" ? (
                <ListGroupItem>{skill.seven}</ListGroupItem>
              ) : null}
              {skill.eight !== "" ? (
                <ListGroupItem>{skill.eight}</ListGroupItem>
              ) : null}
              {/* <ListGroupItem>{skill.four}</ListGroupItem>
              <ListGroupItem>{skill.five}</ListGroupItem>
              <ListGroupItem>{skill.six}</ListGroupItem>
              <ListGroupItem>{skill.seven}</ListGroupItem>
              <ListGroupItem>{skill.eight}</ListGroupItem> */}
            </ListGroup>
          </Card>
        ))}
      </Row>
      <Pagination
        totalPros={pros.length}
        currentPage={currentPage}
        perPage={perPage}
        onPageChange={handlePageChange}
      />
    </Col>
  );
};

export default Skills;
